pragma solidity 0.8.3;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

import "../interfaces/ICommunityIssuance.sol";

import "../interfaces/IMYOToken.sol";
import "../dependencies/BaseMath.sol";
import "../dependencies/CheckContract.sol";

contract CommunityIssuance is ICommunityIssuance, Ownable, CheckContract {
	using SafeMath for uint256;

	uint256 internal constant DECIMAL_PRECISION = 1e18;

	// --- Data ---

	string public constant NAME = "CommunityIssuance";

	uint256 public constant SECONDS_IN_ONE_MINUTE = 60;

	/* The issuance factor F determines the curvature of the issuance curve.
	 *
	 * Minutes in one year: 60*24*365 = 525600
	 *
	 * For 50% of remaining tokens issued each year, with minutes as time units, we have:
	 *
	 * F ** 525600 = 0.5
	 *
	 * Re-arranging:
	 *
	 * 525600 * ln(F) = ln(0.5)
	 * F = 0.5 ** (1/525600)
	 * F = 0.999998681227695000
	 */
	uint256 public constant ISSUANCE_FACTOR = 999998681227695000;

	/*
	 * The community MYO supply cap is the starting balance of the Community Issuance contract.
	 * It should be minted to this contract by MYOToken, when the token is deployed.
	 *
	 * Set to 25M (1/4) of total MYO supply.
	 */
	uint256 public constant MYOSupplyCap = 25e24; // 25 million

	IMYOToken public myoToken;
	address public governor;

	uint256 public totalMYOIssued;
	uint256 public immutable deploymentTime;

	// --- Functions ---

	constructor() {
		deploymentTime = block.timestamp;
	}

	function setAddresses(address _myoTokenAddress, address _governor) external override onlyOwner {
		checkContract(_myoTokenAddress);
		checkContract(_governor);

		myoToken = IMYOToken(_myoTokenAddress);
		governor = _governor;

		// When MYOToken deployed, it should have transferred CommunityIssuance's MYO entitlement
		uint256 MYOBalance = myoToken.balanceOf(address(this));
		assert(MYOBalance >= MYOSupplyCap);

		emit MYOTokenAddressSet(_myoTokenAddress);

		renounceOwnership();
	}

	function issueMYO() external override returns (uint256) {
		_requireCallerIsGovernor();

		uint256 latestTotalMYOIssued = MYOSupplyCap.mul(_getCumulativeIssuanceFraction()).div(DECIMAL_PRECISION);
		uint256 issuance = latestTotalMYOIssued.sub(totalMYOIssued);

		totalMYOIssued = latestTotalMYOIssued;
		emit TotalMYOIssuedUpdated(latestTotalMYOIssued);

		return issuance;
	}

	/* Gets 1-f^t    where: f < 1

    f: issuance factor that determines the shape of the curve
    t:  time passed since last LQTY issuance event  */
	function _getCumulativeIssuanceFraction() internal view returns (uint256) {
		// Get the time passed since deployment
		uint256 timePassedInMinutes = block.timestamp.sub(deploymentTime).div(SECONDS_IN_ONE_MINUTE);

		// f^t
		uint256 power = BaseMath._decPow(ISSUANCE_FACTOR, timePassedInMinutes);

		//  (1 - f^t)
		uint256 cumulativeIssuanceFraction = (uint256(DECIMAL_PRECISION).sub(power));
		assert(cumulativeIssuanceFraction <= DECIMAL_PRECISION); // must be in range [0,1]

		return cumulativeIssuanceFraction;
	}

	function sendMYO(address _account, uint256 _amount) external override {
		_requireCallerIsGovernor();

		myoToken.transfer(_account, _amount);
	}

	// --- 'require' functions ---

	function _requireCallerIsGovernor() internal view {
		require(msg.sender == governor, "CommunityIssuance: caller is not governor");
	}
}
