import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract Core is ICore, Ownable, CheckContract {
	IERC20 public override gemToken;
	IPAIToken public override paiToken;
	IMYOToken public override myoToken;

	ISortedTroves public override sortedTroves;

	IVault public override vault;
	IPriceFeed public override priceFeed;
	IDefaultPool public override defaultPool;
	IActivePool public override activePool;
	ICollSurplusPool public override collSurplusPool;
	IBorrowerOperations public override borrowerOperations;
	IMYOStaking public override myoStaking;

	function setAddresses(
		address _gemTokenAddress,
		address _paiTokenAddress,
		address _myoTokenAddress,
		address _sortedTrovesAddress,
		address _vaultAddress,
		address _myoStakingAddress,
		address _priceFeedAddress,
		address _activePoolAddress,
		address _defaultPoolAddress,
		address _collSurplusPoolAddress,
		address _borrowerOperationsAddress
	) external override onlyOwner {
		checkContract(_gemTokenAddress);
		checkContract(_paiTokenAddress);
		checkContract(_myoTokenAddress);
		checkContract(_sortedTrovesAddress);
		checkContract(_vaultAddress);
		checkContract(_myoStakingAddress);
		checkContract(_priceFeedAddress);
		checkContract(_activePoolAddress);
		checkContract(_defaultPoolAddress);
		checkContract(_collSurplusPoolAddress);
		checkContract(_borrowerOperationsAddress);

		gemToken = IERC20(_gemTokenAddress);
		paiToken = IPAIToken(_paiTokenAddress);
		myoToken = IMYOToken(_myoTokenAddress);

		sortedTroves = ISortedTroves(_sortedTrovesAddress);

		vault = IVault(_vaultAddress);
		myoStaking = IMYOStaking(_myoStakingAddress);
		priceFeed = IPriceFeed(_priceFeedAddress);
		activePool = IActivePool(_activePoolAddress);
		defaultPool = IDefaultPool(_defaultPoolAddress);
		collSurplusPool = ICollSurplusPool(_collSurplusPoolAddress);
		borrowerOperations = IBorrowerOperations(_borrowerOperationsAddress);

		renounceOwnership();
	}
}
