pragma solidity 0.8.3;

// Common interface for the Trove Manager.
interface IBorrowerOperations {
	// --- Events ---

	event TroveCreated(address indexed _borrower, uint256 arrayIndex);
	event TroveUpdated(
		address indexed _borrower,
		uint256 _debt,
		uint256 _coll,
		uint256 stake,
		uint256 _borrowingFee,
		uint8 operation
	);

	event BorrowingFeePaid(address indexed _borrower, uint256 _Fee);

	// --- Functions ---

	function openTrove(
		uint256 _maxFee,
		uint256 _debt,
		uint256 _coll,
		address _upperHint,
		address _lowerHint
	) external;

	function addColl(
		uint256 _coll,
		address _upperHint,
		address _lowerHint
	) external;

	function withdrawColl(
		uint256 _coll,
		address _upperHint,
		address _lowerHint
	) external;

	function withdrawPAI(
		uint256 _maxFee,
		uint256 _amount,
		address _upperHint,
		address _lowerHint
	) external;

	function repayPAI(
		uint256 _amount,
		address _upperHint,
		address _lowerHint
	) external;

	function closeTrove() external;

	function adjustTrove(
		uint256 _collChange,
		bool _isCollIncrease,
		uint256 _debtChange,
		bool _isDebtIncrease,
		address _upperHint,
		address _lowerHint,
		uint256 _maxFeePercentage
	) external;

	function claimCollateral() external;
}
