pragma solidity ^0.8.3;

import "./IBase.sol";

interface IVault is IBase {
	// --- Events ---

	event TroveUpdated(
		address indexed _borrower,
		uint256 _debt,
		uint256 _coll,
		uint256 _borrowingFee,
		uint256 _stake,
		TroveOperation _operation
	);
	event TotalStakesUpdated(uint256 _newTotalStakes);
	event TroveSnapshotsUpdated(uint256 _L_Coll, uint256 _L_Debt);

	event BaseRateUpdated(uint256 _baseRate);
	event LastFeeOpTimeUpdated(uint256 _lastFeeOpTime);

	event Liquidation(uint256 _liquidatedDebt, uint256 _liquidatedColl, uint256 _gasCompensation);
	event Redemption(
		uint256 _attemptedPAIAmount,
		uint256 _actualPAIAmount,
		uint256 _collSent,
		uint256 _collFee
	);
	event TroveLiquidated(address indexed _borrower, uint256 _debt, uint256 _coll, uint8 operation);
	event SystemSnapshotsUpdated(uint256 _totalStakesSnapshot, uint256 _totalCollateralSnapshot);
	event LTermsUpdated(uint256 _L_Coll, uint256 _L_Debt);
	event TroveIndexUpdated(address _borrower, uint256 _newIndex);

	function baseRate() external view returns (uint256);

	// Borrowing Fee Functions

	function getTroveOwnersCount() external view returns (uint256);

	function getTroveFromTroveOwnersArray(uint256 _index) external view returns (address);

	// Collateralization Ratios
	function getNominalICR(address _borrower) external view returns (uint256);

	function getCurrentICR(address _borrower, uint256 _price) external view returns (uint256);

	function getTCR(uint256 _price) external view returns (uint256);

	// Liquidations
	function liquidate(address _borrower) external;

	function liquidateTroves(uint256 _n) external;

	function batchLiquidateTroves(address[] calldata _troveArray) external;

	function redeemCollateral(
		uint256 _paiAmount,
		address _firstRedemptionHint,
		address _upperPartialRedemptionHint,
		address _lowerPartialRedemptionHint,
		uint256 _partialRedemptionHintNICR,
		uint256 _maxIterations,
		uint256 _maxFee
	) external;

	function updateStakeAndTotalStakes(address _borrower) external returns (uint256);

	function updateTroveRewardSnapshots(address _borrower) external;

	function addTroveOwnerToArray(address _borrower) external returns (uint256 index);

	function applyPendingRewards(address _borrower) external;

	function getPendingCollReward(address _borrower) external view returns (uint256);

	function getPendingDebtReward(address _borrower) external view returns (uint256);

	function hasPendingRewards(address _borrower) external view returns (bool);

	function getEntireDebtAndColl(address _borrower)
		external
		view
		returns (
			uint256 debt,
			uint256 coll,
			uint256 pendingDebtReward,
			uint256 pendingCollReward
		);

	function closeTrove(address _borrower) external;

	function removeStake(address _borrower) external;

	// Borrowing and Redemption Fees

	function getRedemptionRate() external view returns (uint256);

	function getRedemptionRateWithDecay() external view returns (uint256);

	function getRedemptionFeeWithDecay(uint256 _collDrawn) external view returns (uint256);

	function getBorrowingRate() external view returns (uint256);

	function getBorrowingRateWithDecay() external view returns (uint256);

	function getBorrowingFee(uint256 _debt, uint256 _price) external view returns (uint256);

	function getBorrowingFeeWithDecay(uint256 _debt, uint256 _price)
		external
		view
		returns (uint256);

	function decayBaseRateFromBorrowing() external;

	// Trove Management
	function increaseTroveColl(address _borrower, uint256 _collIncrease) external returns (uint256);

	function decreaseTroveColl(address _borrower, uint256 _collDecrease) external returns (uint256);

	function increaseTroveDebt(address _borrower, uint256 _debtIncrease) external returns (uint256);

	function decreaseTroveDebt(address _borrower, uint256 _debtDecrease) external returns (uint256);

	function setTroveStatus(address _borrower, uint256 _num) external;

	function getTroveStatus(address _borrower) external view returns (TroveStatus);

	function getTroveStake(address _borrower) external view returns (uint256);

	function getTroveDebt(address _borrower) external view returns (uint256);

	function getTroveColl(address _borrower) external view returns (uint256);
}
