pragma solidity 0.8.3;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";

import "./CoreRef.sol";
import "../interfaces/IBorrowerOperations.sol";

import "hardhat/console.sol";

contract BorrowerOperations is CoreRef, IBorrowerOperations {
	using SafeMath for uint256;

	// solhint-disable-next-line no-empty-blocks
	constructor(address _core) CoreRef(_core) {}

	enum BorrowerOperation {
		openTrove,
		closeTrove,
		adjustTrove
	}

	// OPEN TROVE
	//
	// uint256 _maxFeePercentage,
	// uint256 _debt,
	// uint256 _coll,
	// address _upperHint,
	// address _lowerHint
	//
	// Fetch Price and determine if in recovery mode.
	// Verify fee
	// Determine Borrowing Fee
	// Calculate net Collateral as: collateral - borrowing fee
	// Require sufficient net collateral to cover for gas compensation reserve
	// Determine minimum collateralization ratio is meet; trove collateral (for borrwing capacity) is net collateral - Gas Comp
	// Adjust trove
	// Insert Sorted
	// Transfer Collateral
	// Transfer Debt
	// Emit Events

	function openTrove(
		uint256 _maxFeePercentage,
		uint256 _debt,
		uint256 _coll,
		address _upperHint,
		address _lowerHint
	) external override {
		_requireTroveisNotActive(msg.sender);

		uint256 price = priceFeed().fetchPrice();
		bool isRecoveryMode = _checkRecoveryMode(price);

		_requireValidMaxFeePercentage(_maxFeePercentage, isRecoveryMode);

		uint256 borrowingFee;
		uint256 netColl = _coll;

		if (!isRecoveryMode) {
			// Calc fee, verify user acceptance
			borrowingFee = _calcBorrowingFee(_debt, price);
			_requireUserAcceptsFee(
				borrowingFee,
				_debt.mul(DECIMAL_PRECISION).div(price),
				_maxFeePercentage
			);

			assert(netColl > borrowingFee);
			netColl = netColl.sub(borrowingFee);
		}

		// GAS COMP is set aside in the ActivePool;
		// netColl available in the trove is coll minus DAI_GAS_COMPENSATION
		_requireAtLeastGasCompAndBorrowingFeeCollateral(netColl, borrowingFee);
		netColl = netColl.sub(DAI_GAS_COMPENSATION);

		// Borrowing capacity depends on collateral minus gas comp and borrowing fee
		uint256 ICR = BaseMath._computeCR(netColl, _debt, price);
		uint256 NICR = BaseMath._computeNominalCR(netColl, _debt);

		if (isRecoveryMode) {
			_requireICRisAboveCCR(ICR);
		} else {
			_requireICRisAboveMCR(ICR);
			uint256 newTCR = _getNewTCRFromTroveChange(netColl, true, _debt, true, price); // bools: coll increase, debt increase
			_requireNewTCRisAboveCCR(newTCR);
		}

		// Set the trove struct's properties
		vault().setTroveStatus(msg.sender, 1);
		vault().increaseTroveColl(msg.sender, netColl);
		vault().increaseTroveDebt(msg.sender, _debt);

		vault().updateTroveRewardSnapshots(msg.sender);
		uint256 stake = vault().updateStakeAndTotalStakes(msg.sender);

		sortedTroves().insert(msg.sender, NICR, _upperHint, _lowerHint);
		uint256 arrayIndex = vault().addTroveOwnerToArray(msg.sender);

		_withdrawPAI(msg.sender, _debt);

		// Move Collateral to ActivePool
		_activePoolAddColl(msg.sender, _coll); // Transfer all the collateral supplied ( including borrowing Fee + GAS_COMP to ActivePool)j
		activePool().increaseGasCompensationBalance(); // Increment gas compensation tracker in ActivePool

		// Send fee to MYO staking contract
		_transferFeeToStacking(msg.sender, borrowingFee);

		emit TroveCreated(msg.sender, arrayIndex);
		emit TroveUpdated(
			msg.sender,
			_debt,
			netColl,
			stake,
			borrowingFee,
			uint8(BorrowerOperation.openTrove)
		);
	}

	function closeTrove() external override {
		_requireActiveTrove(msg.sender);

		uint256 price = priceFeed().fetchPrice();
		_requireNotInRecoveryMode(price);

		vault().applyPendingRewards(msg.sender);

		uint256 coll = vault().getTroveColl(msg.sender);
		uint256 debt = vault().getTroveDebt(msg.sender);

		_requireSufficientBalance(msg.sender, debt);

		uint256 newTCR = _getNewTCRFromTroveChange(coll, false, debt, false, price);
		_requireNewTCRisAboveCCR(newTCR);

		vault().removeStake(msg.sender);
		vault().closeTrove(msg.sender);

		emit TroveUpdated(msg.sender, 0, 0, 0, 0, uint8(BorrowerOperation.closeTrove));

		// Burn the repaid LUSD from the user's balance and the gas compensation from the Gas Pool
		_repayPAI(msg.sender, debt);

		activePool().decreaseGasCompensationBalance();

		// Send the collateral back to the user (Including GAS Comp reserved on openTrove)
		activePool().sendColl(msg.sender, coll.add(DAI_GAS_COMPENSATION));
	}

	function addColl(
		uint256 _collIncrease,
		address _upperHint,
		address _lowerHint
	) external override {
		_adjustTrove(msg.sender, _collIncrease, true, 0, false, _upperHint, _lowerHint, 0);
	}

	// Withdraw ETH collateral from a trove
	function withdrawColl(
		uint256 _collWithdrawal,
		address _upperHint,
		address _lowerHint
	) external override {
		_adjustTrove(msg.sender, _collWithdrawal, false, 0, false, _upperHint, _lowerHint, 0);
	}

	// Withdraw LUSD tokens from a trove: mint new LUSD tokens to the owner, and increase the trove's debt accordingly
	function withdrawPAI(
		uint256 _maxFeePercentage,
		uint256 _paiWithdrawal,
		address _upperHint,
		address _lowerHint
	) external override {
		// TODO: Add borrowing Fee
		_adjustTrove(
			msg.sender,
			0,
			false,
			_paiWithdrawal,
			true,
			_upperHint,
			_lowerHint,
			_maxFeePercentage
		);
	}

	// Repay LUSD tokens to a Trove: Burn the repaid LUSD tokens, and reduce the trove's debt accordingly
	function repayPAI(
		uint256 _paiAmount,
		address _upperHint,
		address _lowerHint
	) external override {
		_adjustTrove(msg.sender, 0, false, _paiAmount, false, _upperHint, _lowerHint, 0);
	}

	// solhint-disable-next-line contract-name-camelcase
	struct LocalVariables_AdjustTrove {
		uint256 price;
		bool isRecoveryMode;
		uint256 debt;
		uint256 coll;
		uint256 newDebt;
		uint256 newColl;
		uint256 oldICR;
		uint256 newICR;
		uint256 stake;
		uint256 newNICR;
		uint256 borrowingFee;
	}

	function adjustTrove(
		uint256 _collChange,
		bool _isCollIncrease,
		uint256 _debtChange,
		bool _isDebtIncrease,
		address _upperHint,
		address _lowerHint,
		uint256 _maxFeePercentage
	) external override {
		_adjustTrove(
			msg.sender,
			_collChange,
			_isCollIncrease,
			_debtChange,
			_isDebtIncrease,
			_upperHint,
			_lowerHint,
			_maxFeePercentage
		);
	}

	function _adjustTrove(
		address _borrower,
		uint256 _collChange,
		bool _isCollIncrease,
		uint256 _debtChange,
		bool _isDebtIncrease,
		address _upperHint,
		address _lowerHint,
		uint256 _maxFeePercentage
	) internal {
		_requireActiveTrove(_borrower);
		_requireNonZeroAdjustment(_collChange, _debtChange);

		LocalVariables_AdjustTrove memory vars;

		vars.price = priceFeed().fetchPrice();
		vars.isRecoveryMode = _checkRecoveryMode(vars.price);

		if (_isDebtIncrease) {
			_requireValidMaxFeePercentage(_maxFeePercentage, vars.isRecoveryMode);
			_requireNonZeroDebtChange(_debtChange);

			if (!vars.isRecoveryMode) {
				vars.borrowingFee = _calcBorrowingFee(_debtChange, vars.price);
				_requireUserAcceptsFee(
					vars.borrowingFee,
					_debtChange.mul(DECIMAL_PRECISION).div(vars.price),
					_maxFeePercentage
				);
			}
		}

		// Apply redistributions
		vault().applyPendingRewards(_borrower);

		vars.debt = vault().getTroveDebt(_borrower);
		vars.coll = vault().getTroveColl(_borrower);

		// Collateral adjustment should be enough to pay for borrowing fee and leave gas comp locked.
		_requireValidCollAdjustment(vars.coll, _collChange, _isCollIncrease, vars.borrowingFee);

		vars.oldICR = BaseMath._computeCR(vars.coll, vars.debt, vars.price);
		vars.newICR = _getNewICRFromTroveChange(
			vars.coll,
			vars.debt,
			_collChange,
			_isCollIncrease,
			_debtChange,
			_isDebtIncrease,
			vars.borrowingFee,
			vars.price
		);

		_requireValidTroveAdjustment(
			vars.isRecoveryMode,
			_collChange,
			_isCollIncrease,
			_debtChange,
			_isDebtIncrease,
			vars.oldICR,
			vars.newICR,
			vars.price
		);

		(vars.newColl, vars.newDebt) = _updateTroveFromAdjustment(
			_borrower,
			_collChange,
			_isCollIncrease,
			_debtChange,
			_isDebtIncrease,
			vars.borrowingFee
		);

		// When the adjustment is a debt repayment, check it's a valid amount and that the caller has enough PAI
		if (!_isDebtIncrease && _debtChange > 0) {
			_requireValidRepayment(_borrower, vars.debt);
			_requireSufficientBalance(_borrower, _debtChange);
		}

		vars.stake = vault().updateStakeAndTotalStakes(_borrower);

		// Re-insert trove in to the sorted list
		vars.newNICR = _getNewNominalICRFromTroveChange(
			vars.coll,
			vars.debt,
			_collChange,
			_isCollIncrease,
			_debtChange,
			_isDebtIncrease,
			vars.borrowingFee
		);

		sortedTroves().reInsert(_borrower, vars.newNICR, _upperHint, _lowerHint);

		emit TroveUpdated(
			_borrower,
			vars.newDebt,
			vars.newColl,
			vars.stake,
			vars.borrowingFee,
			uint8(BorrowerOperation.adjustTrove)
		);

		// Send fee to MYO staking contract
		_transferFeeToStacking(_borrower, vars.borrowingFee);

		// Get Coll Gem from msg.sender and issue new PAI
		_moveCollAndDebtFromAdjustment(
			_borrower,
			_collChange,
			_isCollIncrease,
			_debtChange,
			_isDebtIncrease
		);
	}

	// Update trove's coll and debt based on whether they increase or decrease
	function _updateTroveFromAdjustment(
		address _borrower,
		uint256 _collChange,
		bool _isCollIncrease,
		uint256 _debtChange,
		bool _isDebtIncrease,
		uint256 _borrowingFee
	) internal returns (uint256, uint256) {
		uint256 newColl = (_isCollIncrease)
			? vault().increaseTroveColl(_borrower, _collChange)
			: vault().decreaseTroveColl(_borrower, _collChange);
		uint256 newDebt = (_isDebtIncrease)
			? vault().increaseTroveDebt(_borrower, _debtChange)
			: vault().decreaseTroveDebt(_borrower, _debtChange);

		newColl = vault().decreaseTroveColl(_borrower, _borrowingFee);

		return (newColl, newDebt);
	}

	function _moveCollAndDebtFromAdjustment(
		address _borrower,
		uint256 _collChange,
		bool _isCollIncrease,
		uint256 _debtChange,
		bool _isDebtIncrease
	) internal {
		if (_debtChange > 0) {
			if (_isDebtIncrease) {
				_withdrawPAI(_borrower, _debtChange);
			} else {
				_repayPAI(_borrower, _debtChange);
			}
		}

		if (_collChange > 0) {
			if (_isCollIncrease) {
				_activePoolAddColl(_borrower, _collChange);
			} else {
				activePool().sendColl(_borrower, _collChange);
			}
		}
	}

	// solhint-disable-next-line no-empty-blocks
	function claimCollateral() external override {}

	function _getNewTCRFromTroveChange(
		uint256 _collChange,
		bool _isCollIncrease,
		uint256 _debtChange,
		bool _isDebtIncrease,
		uint256 _price
	) internal view returns (uint256) {
		uint256 totalColl = getEntireSystemColl();
		uint256 totalDebt = getEntireSystemDebt();

		totalColl = _isCollIncrease ? totalColl.add(_collChange) : totalColl.sub(_collChange);
		totalDebt = _isDebtIncrease ? totalDebt.add(_debtChange) : totalDebt.sub(_debtChange);

		uint256 newTCR = BaseMath._computeCR(totalColl, totalDebt, _price);
		return newTCR;
	}

	// Compute the new collateral ratio, considering the change in coll and debt. Assumes 0 pending rewards.
	function _getNewICRFromTroveChange(
		uint256 _coll,
		uint256 _debt,
		uint256 _collChange,
		bool _isCollIncrease,
		uint256 _debtChange,
		bool _isDebtIncrease,
		uint256 _borrowingFee,
		uint256 _price
	) internal pure returns (uint256) {
		(uint256 newColl, uint256 newDebt) = _getNewTroveAmounts(
			_coll,
			_debt,
			_collChange,
			_isCollIncrease,
			_debtChange,
			_isDebtIncrease,
			_borrowingFee
		);

		uint256 newICR = BaseMath._computeCR(newColl, newDebt, _price);
		return newICR;
	}

	// Compute the new collateral ratio, considering the change in coll and debt. Assumes 0 pending rewards.
	function _getNewNominalICRFromTroveChange(
		uint256 _coll,
		uint256 _debt,
		uint256 _collChange,
		bool _isCollIncrease,
		uint256 _debtChange,
		bool _isDebtIncrease,
		uint256 _borrowingFee
	) internal pure returns (uint256) {
		(uint256 newColl, uint256 newDebt) = _getNewTroveAmounts(
			_coll,
			_debt,
			_collChange,
			_isCollIncrease,
			_debtChange,
			_isDebtIncrease,
			_borrowingFee
		);

		uint256 newNICR = BaseMath._computeNominalCR(newColl, newDebt);
		return newNICR;
	}

	function _getNewTroveAmounts(
		uint256 _coll,
		uint256 _debt,
		uint256 _collChange,
		bool _isCollIncrease,
		uint256 _debtChange,
		bool _isDebtIncrease,
		uint256 _borrowingFee
	) internal pure returns (uint256, uint256) {
		uint256 newColl = _coll;
		uint256 newDebt = _debt;

		newColl = _isCollIncrease ? _coll.add(_collChange) : _coll.sub(_collChange);
		newDebt = _isDebtIncrease ? _debt.add(_debtChange) : _debt.sub(_debtChange);

		assert(newColl >= _borrowingFee);
		newColl = newColl.sub(_borrowingFee);

		return (newColl, newDebt);
	}

	// --- Borrowing fee functions ---

	// Calculate borrowing fee based on trove's debt; fee is charged in gem tokens and
	// sent to the stacking contract for distribution.
	// function _triggerBorrowingFee(
	// 	address _borrower,
	// 	uint256 _debt,
	// 	uint256 _maxFeePercentage,
	// 	uint256 _price
	// ) internal returns (uint256) {
	// 	vault().decayBaseRateFromBorrowing(); // decay the baseRate state variable

	// 	// Get Borrowing Fee in GEMs (Fees are paid in collateral token)
	// 	uint256 borrowingFee_gem = vault().getBorrowingFee(_debt, _price);

	// 	_requireUserAcceptsFee(
	// 		borrowingFee_gem,
	// 		_debt.mul(DECIMAL_PRECISION).div(_price),
	// 		_maxFeePercentage
	// 	);

	// 	// Send fee to MYO staking contract
	// 	_transferFeeToStacking(_borrower, borrowingFee_gem);

	// 	return borrowingFee_gem;
	// }

	// Calculate borrowing fee based on trove's debt; fee is charged in gem tokens and
	// sent to the stacking contract for distribution.
	function _calcBorrowingFee(uint256 _debt, uint256 _price) internal returns (uint256) {
		vault().decayBaseRateFromBorrowing(); // decay the baseRate state variable

		// Get Borrowing Fee in GEMs (Fees are paid in collateral token)
		uint256 borrowingFee = vault().getBorrowingFee(_debt, _price);

		return borrowingFee;
	}

	function _transferFeeToStacking(address _borrower, uint256 _fee) internal {
		emit BorrowingFeePaid(_borrower, _fee);

		activePool().transferFeeToStacking(_fee);
	}

	// Send GEM tokens to Active Pool and increase its recorded Coll balance
	function _activePoolAddColl(address _borrower, uint256 _amount) internal {
		activePool().receiveColl(_borrower, _amount);
	}

	// function _gasPoolAddTokens(address _sender, uint _amount) internal {
	// 	gemToken().transferFrom(_sender, address(gasPool()), _amount);
	// }

	// Issue the specified amount of PAI to _borrower and increases the total active debt
	function _withdrawPAI(address _borrower, uint256 _amount) internal {
		activePool().increaseDebt(_amount);
		paiToken().mint(_borrower, _amount);
	}

	// Burn the specified amount of PAI from _borrower and decreases the total active debt
	function _repayPAI(address _borrower, uint256 _amount) internal {
		activePool().decreaseDebt(_amount);
		paiToken().burn(_borrower, _amount);
	}

	// Require wrappers
	function _requireActiveTrove(address _borrower) internal view {
		require(
			vault().getTroveStatus(_borrower) == TroveStatus.active,
			"BorrowerOps: Trove must be active"
		);
	}

	function _requireValidTroveAdjustment(
		bool _isRecoveryMode,
		uint256 _collChange,
		bool _isCollIncrease,
		uint256 _debtChange,
		bool _isDebtIncrease,
		uint256 _oldICR,
		uint256 _newICR,
		uint256 _price
	) internal view {
		/*
		 *In Recovery Mode, only allow:
		 *
		 * - Pure collateral top-up
		 * - Pure debt repayment
		 * - Collateral top-up with debt repayment
		 * - A debt increase combined with a collateral top-up which makes the ICR >= 150% and improves the ICR (and by extension improves the TCR).
		 *
		 * In Normal Mode, ensure:
		 *
		 * - The new ICR is above MCR
		 * - The adjustment won't pull the TCR below CCR
		 */
		if (_isRecoveryMode) {
			_requireNoCollWithdrawal(_collChange, _isCollIncrease);
			if (_isDebtIncrease) {
				_requireICRisAboveCCR(_newICR);
				_requireNewICRisAboveOldICR(_newICR, _oldICR);
			}
		} else {
			// if Normal Mode
			_requireICRisAboveMCR(_newICR);
			uint256 newTCR = _getNewTCRFromTroveChange(
				_collChange,
				_isCollIncrease,
				_debtChange,
				_isDebtIncrease,
				_price
			);
			_requireNewTCRisAboveCCR(newTCR);
		}
	}

	function _requireNoCollWithdrawal(uint256 _collChange, bool _isCollIncrease) internal pure {
		require(
			_isCollIncrease == true || _collChange == 0,
			"BorrowerOps: Collateral withdrawal not permitted Recovery Mode"
		);
	}

	// Valid adjustment is when the collateral left in the trove is greater than any
	// pending borrowing fee that needs to be charged plus Gas Comp.
	// borrowingFee can be 0, in that case min collateral is equal to Gas Comp.
	function _requireValidCollAdjustment(
		uint256 _coll,
		uint256 _collChange,
		bool _isCollIncrease,
		uint256 _borrowingFee
	) internal pure {
		uint256 minCollateral = _borrowingFee.add(DAI_GAS_COMPENSATION);

		if (_isCollIncrease) {
			// Coll increase could still leave collateral below minimum requirement depending on the borrowing fees
			// that need to be applied.
			require(
				_coll.add(_collChange) >= minCollateral,
				"BorrowerOps: Not enough collateral for adjustment"
			);
		} else {
			// A coll decrease requires a trove with  minimum collateral equal to Gas Comp (if borrowing fee is 0),
			// so coll has to be greater than the amount withdraw plus min collateral
			require(
				_coll >= _collChange.add(minCollateral),
				"BorrowerOps: Not enough collateral for adjustment"
			);
		}
	}

	function _requireAtLeastGasCompAndBorrowingFeeCollateral(uint256 _coll, uint256 _borrowingFee)
		internal
		pure
	{
		require(
			_coll >= DAI_GAS_COMPENSATION.add(_borrowingFee),
			"BorrowerOps: Not enough collateral for Gas Compensation & Borrowing Fee"
		);
	}

	function _requireNonZeroAdjustment(uint256 _collChange, uint256 _debtChange) internal pure {
		require(
			_collChange != 0 || _debtChange != 0,
			"BorrowerOps: There must be either a collateral change or a debt change"
		);
	}

	function _requireNonZeroDebtChange(uint256 _debtChange) internal pure {
		require(_debtChange > 0, "BorrowerOps: Non-Zero debt change is required");
	}

	function _requireSufficientBalance(address _borrower, uint256 _repayment) internal view {
		uint256 balance = paiToken().balanceOf(_borrower);
		require(balance >= _repayment, "BorrowerOps: Borrower does not have enough PAI balance");
	}

	function _requireValidRepayment(address _borrower, uint256 _amount) internal view {
		uint256 currentDebt = vault().getTroveDebt(_borrower);
		require(_amount <= currentDebt, "BorrowerOps: Trove debt is less than repayment amount");
	}

	function _requireNewICRisAboveOldICR(uint256 _newICR, uint256 _oldICR) internal pure {
		require(
			_newICR >= _oldICR,
			"BorrowerOps: Cannot decrease your Trove's ICR in Recovery Mode"
		);
	}

	function _requireICRisAboveMCR(uint256 _newICR) internal pure {
		require(
			_newICR >= MCR,
			"BorrowerOps: An operation that would result in ICR < MCR is not permitted"
		);
	}

	function _requireICRisAboveCCR(uint256 _newICR) internal pure {
		require(_newICR >= CCR, "BorrowerOps: Operation must leave trove with ICR >= CCR");
	}

	function _requireNewTCRisAboveCCR(uint256 _newTCR) internal pure {
		require(
			_newTCR >= CCR,
			"BorrowerOps: An operation that would result in TCR < CCR is not permitted"
		);
	}

	function _requireTroveisNotActive(address _borrower) internal view {
		require(
			vault().getTroveStatus(_borrower) != TroveStatus.active,
			"BorrowerOps: Trove already exists"
		);
	}

	function _requireNotInRecoveryMode(uint256 _price) internal view {
		require(
			!_checkRecoveryMode(_price),
			"BorrowerOps: Operation not permitted during Recovery Mode"
		);
	}

	function _requireValidMaxFeePercentage(uint256 _maxFeePercentage, bool _isRecoveryMode)
		internal
		pure
	{
		if (_isRecoveryMode) {
			require(
				_maxFeePercentage <= DECIMAL_PRECISION,
				"BorrowerOps: Max fee percentage must less than or equal to 100%"
			);
		} else {
			require(
				_maxFeePercentage >= BORROWING_FEE_FLOOR && _maxFeePercentage <= DECIMAL_PRECISION,
				"BorrowerOps: Max fee percentage must be between 0.5% and 100%"
			);
		}
	}
}
