pragma solidity 0.8.3;

// CAUTION
// This version of SafeMath should only be used with Solidity 0.8 or later,
// because it relies on the compiler's built in overflow checks.
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

import "../dependencies/CheckContract.sol";
import "../dependencies/Constants.sol";
import "../dependencies/BaseMath.sol";

import "../interfaces/ICore.sol";

import "../interfaces/IMYOStaking.sol";
import "../interfaces/IMYOToken.sol";
import "../interfaces/IPAIToken.sol";

import "../interfaces/IPriceFeed.sol";
import "../interfaces/IDefaultPool.sol";
import "../interfaces/IActivePool.sol";
import "../interfaces/IBorrowerOperations.sol";

contract CoreRef is CheckContract, Constants, IBase {
	using SafeMath for uint256;

	ICore public immutable core;

	constructor(address _core) {
		checkContract(_core);
		core = ICore(_core);
	}

	// function setAddresses(
	//     address _core
	// )
	//     external
	//     onlyOwner
	// {
	//     checkContract(_core);
	//     core = ICore(_core);
	//     renounceOwnership();
	// }

	modifier onlyVault() {
		require(msg.sender == address(core.vault()), "CoreRef: Caller must be Vault");
		_;
	}

	modifier onlyBO() {
		require(
			msg.sender == address(core.borrowerOperations()),
			"CoreRef: Caller must be BorrowerOperations"
		);
		_;
	}

	modifier onlyActivePool() {
		require(msg.sender == address(core.activePool()), "CoreRef: Caller must be ActivePool");
		_;
	}

	// Getters

	function gemToken() public view returns (IERC20) {
		return core.gemToken();
	}

	function paiToken() public view returns (IPAIToken) {
		return core.paiToken();
	}

	function myoToken() public view returns (IMYOToken) {
		return core.myoToken();
	}

	function sortedTroves() public view returns (ISortedTroves) {
		return core.sortedTroves();
	}

	function vault() public view returns (IVault) {
		return core.vault();
	}

	function myoStaking() public view returns (IMYOStaking) {
		return core.myoStaking();
	}

	function priceFeed() public view returns (IPriceFeed) {
		return core.priceFeed();
	}

	function activePool() public view returns (IActivePool) {
		return core.activePool();
	}

	function defaultPool() public view returns (IDefaultPool) {
		return core.defaultPool();
	}

	function collSurplusPool() public view returns (ICollSurplusPool) {
		return core.collSurplusPool();
	}

	function borrowerOperations() public view returns (IBorrowerOperations) {
		return core.borrowerOperations();
	}

	// Helper Functions

	function getEntireSystemColl() public view returns (uint256 entireSystemColl) {
		uint256 activeColl = activePool().getColl();
		uint256 liquidatedColl = defaultPool().getColl();

		return activeColl.add(liquidatedColl);
	}

	function getEntireSystemDebt() public view returns (uint256 entireSystemDebt) {
		uint256 activeDebt = activePool().getDebt();
		uint256 closedDebt = defaultPool().getDebt();

		return activeDebt.add(closedDebt);
	}

	function _getTCR(uint256 _price) internal view returns (uint256 TCR) {
		uint256 entireSystemColl = getEntireSystemColl();
		uint256 entireSystemDebt = getEntireSystemDebt();

		TCR = BaseMath._computeCR(entireSystemColl, entireSystemDebt, _price);

		return TCR;
	}

	function _checkRecoveryMode(uint256 _price) internal view returns (bool) {
		uint256 TCR = _getTCR(_price);

		return TCR < CCR;
	}

	function _getNetCollateral(uint256 _collateral) internal pure returns (uint256) {
		return
			_collateral.sub(
				DAI_GAS_COMPENSATION,
				"Core: Insuficient Net Collateral to cover GAS Comp"
			);
	}

	function _getGrossColl(uint256 _debt) internal pure returns (uint256) {
		return _debt.add(DAI_GAS_COMPENSATION);
	}

	function _requireUserAcceptsFee(
		uint256 _gemFee,
		uint256 _gemAmount,
		uint256 _maxFeePercentage
	) internal pure {
		uint256 feePercentage = _gemFee.mul(DECIMAL_PRECISION).div(_gemAmount);
		require(feePercentage <= _maxFeePercentage, "Fee exceeded provided maximum");
	}
}
","keccak256":"0xaa9810fc762e1323c91469ddb7f130b266b34929e298a53834fcc9a2977b473b","license":"MIT"},"contracts/dependencies/BaseMath.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.3;

// CAUTION
// This version of SafeMath should only be used with Solidity 0.8 or later,
// because it relies on the compiler's built in overflow checks.
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

library BaseMath {
	using SafeMath for uint256;

	uint256 internal constant DECIMAL_PRECISION = 1e18;

	/* Precision for Nominal ICR (independent of price). Rationale for the value:
	 *
	 * - Making it “too high” could lead to overflows.
	 * - Making it “too low” could lead to an ICR equal to zero, due to truncation from Solidity floor division.
	 *
	 * This value of 1e20 is chosen for safety: the NICR will only overflow for numerator > ~1e39 ETH,
	 * and will only truncate to 0 if the denominator is at least 1e20 times greater than the numerator.
	 *
	 */
	uint256 internal constant NICR_PRECISION = 1e20;

	function _min(uint256 _a, uint256 _b) internal pure returns (uint256) {
		return (_a < _b) ? _a : _b;
	}

	function _max(uint256 _a, uint256 _b) internal pure returns (uint256) {
		return (_a >= _b) ? _a : _b;
	}

	/*
	 * Multiply two decimal numbers and use normal rounding rules:
	 * -round product up if 19'th mantissa digit >= 5
	 * -round product down if 19'th mantissa digit < 5
	 *
	 * Used only inside the exponentiation, _decPow().
	 */
	function decMul(uint256 x, uint256 y) internal pure returns (uint256 decProd) {
		uint256 prod_xy = x.mul(y);

		decProd = prod_xy.add(DECIMAL_PRECISION / 2).div(DECIMAL_PRECISION);
	}

	/*
	 * _decPow: Exponentiation function for 18-digit decimal base, and integer exponent n.
	 *
	 * Uses the efficient "exponentiation by squaring" algorithm. O(log(n)) complexity.
	 *
	 * Called by two functions that represent time in units of minutes:
	 * 1) TroveManager._calcDecayedBaseRate
	 * 2) CommunityIssuance._getCumulativeIssuanceFraction
	 *
	 * The exponent is capped to avoid reverting due to overflow. The cap 525600000 equals
	 * "minutes in 1000 years": 60 * 24 * 365 * 1000
	 *
	 * If a period of > 1000 years is ever used as an exponent in either of the above functions, the result will be
	 * negligibly different from just passing the cap, since:
	 *
	 * In function 1), the decayed base rate will be 0 for 1000 years or > 1000 years
	 * In function 2), the difference in tokens issued at 1000 years and any time > 1000 years, will be negligible
	 */
	function _decPow(uint256 _base, uint256 _minutes) internal pure returns (uint256) {
		if (_minutes > 525600000) {
			_minutes = 525600000;
		} // cap to avoid overflow

		if (_minutes == 0) {
			return DECIMAL_PRECISION;
		}

		uint256 y = DECIMAL_PRECISION;
		uint256 x = _base;
		uint256 n = _minutes;

		// Exponentiation-by-squaring
		while (n > 1) {
			if (n % 2 == 0) {
				x = decMul(x, x);
				n = n.div(2);
			} else {
				// if (n % 2 != 0)
				y = decMul(x, y);
				x = decMul(x, x);
				n = (n.sub(1)).div(2);
			}
		}

		return decMul(x, y);
	}

	function _getAbsoluteDifference(uint256 _a, uint256 _b) internal pure returns (uint256) {
		return (_a >= _b) ? _a.sub(_b) : _b.sub(_a);
	}

	function _computeNominalCR(uint256 _coll, uint256 _debt) internal pure returns (uint256) {
		if (_debt > 0) {
			return _coll.mul(NICR_PRECISION).div(_debt);
		}
		// Return the maximal value for uint256 if the Trove has a debt of 0. Represents "infinite" CR.
		else {
			// if (_debt == 0)
			return 2**256 - 1;
		}
	}

	function _computeCR(
		uint256 _coll,
		uint256 _debt,
		uint256 _price
	) internal pure returns (uint256) {
		if (_debt > 0) {
			uint256 newCollRatio = _coll.mul(_price).div(_debt);
			return newCollRatio;
		}
		// Return the maximal value for uint256 if the Trove has a debt of 0. Represents "infinite" CR.
		else {
			// if (_debt == 0)
			return 2**256 - 1;
		}
	}
}
