pragma solidity ^0.8.3;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract Constants {
	using SafeMath for uint256;

	uint256 public constant DECIMAL_PRECISION = 1e18;

	uint256 public constant SECONDS_IN_ONE_MINUTE = 60;

	// uint256 public constant _100pct = 1000000000000000000; // 1e18 == 100%

	// Minimum collateral ratio for individual troves
	uint256 public constant MCR = 1100000000000000000; // 110%

	// Critical system collateral ratio. If the system's total collateral ratio (TCR) falls below the CCR, Recovery Mode is triggered.
	uint256 public constant CCR = 1500000000000000000; // 150%

	// Amount of DAI to be locked in gas pool on opening troves
	uint256 public constant DAI_GAS_COMPENSATION = 200e16; // 2.00

	// Minimum amount of net PAI debt a trove must have
	uint256 public constant MIN_NET_DEBT = 1800e18;

	uint256 public constant HALF_PERCENT_DIVISOR = 200; // dividing by 200 yields 0.5%

	uint256 public constant BORROWING_FEE_FLOOR = (DECIMAL_PRECISION / 1000) * 5; // 0.005% (1e18 == 100%)

	// During bootsrap period redemptions are not allowed
	uint256 public constant BOOTSTRAP_PERIOD = 14 days;

	// MAX UINT256
	uint256 public MAX_UINT = 2**256 - 1;
}
