pragma solidity 0.8.3;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

import "../interfaces/IDefaultPool.sol";
import "../dependencies/CheckContract.sol";
import "./CoreRef.sol";

/*
 * The Default Pool holds the ETH and LUSD debt (but not LUSD tokens) from liquidations that have been redistributed
 * to active troves but not yet "applied", i.e. not yet recorded on a recipient active trove's struct.
 *
 * When a trove makes an operation that applies its pending ETH and LUSD debt, its pending ETH and LUSD debt is moved
 * from the Default Pool to the Active Pool.
 */

contract DefaultPool is CoreRef, IDefaultPool {
	using SafeMath for uint256;

	string public constant NAME = "DefaultPool";

	uint256 public coll; // deposited Gem tracker
	uint256 public debt; // debt

	constructor(address _core) CoreRef(_core) {}

	// --- Getters for public variables - Required for IDefaultPool interface

	/*
	 * Returns the Coll state variable.
	 * Not necessarily equal to the the contract's raw ERC20 balance - it can be forcibly sent to contracts. */
	function getColl() external view override returns (uint256) {
		return coll;
	}

	/*
	 * Returns the Debt state variable. */
	function getDebt() external view override returns (uint256) {
		return debt;
	}

	// --- Pool functionality ---

	function receiveColl(uint256 _amount) external override returns (bool success) {
		_requireCallerIsVault();

		coll = coll.add(_amount);
		emit DefaultPoolCollBalanceUpdated(coll);

		success = gemToken().transferFrom(msg.sender, address(this), _amount);
		require(success, "DefaultPool: receive ERC20 Collateral Failed");
	}

	function sendCollToActivePool(uint256 _amount) external override {
		_requireCallerIsVault();

		coll = coll.sub(_amount);
		emit DefaultPoolCollBalanceUpdated(coll);
		emit GemSent(address(activePool()), _amount);

		bool success = gemToken().transfer(address(activePool()), _amount);
		require(success, "DefaultPool: sending Collateral Gem failed");
	}

	function increaseDebt(uint256 _amount) external override {
		_requireCallerIsVault();
		debt = debt.add(_amount);
		emit DefaultPoolDebtBalanceUpdated(debt);
	}

	function decreaseDebt(uint256 _amount) external override {
		_requireCallerIsVault();
		debt = debt.sub(_amount);
		emit DefaultPoolDebtBalanceUpdated(debt);
	}

	// --- 'require' functions ---

	function _requireCallerIsVault() internal view {
		require(msg.sender == address(vault()), "DefaultPool: Caller is not the ActivePool");
	}
}
