pragma solidity 0.8.3;

import "../interfaces/IVault.sol";
import "../interfaces/ISortedTroves.sol";
import "../dependencies/BaseMath.sol";
import "../dependencies/CheckContract.sol";
import "../dependencies/Constants.sol";

import "@openzeppelin/contracts/access/Ownable.sol";

contract HintHelpers is Ownable, CheckContract, Constants {
	using SafeMath for uint256;

	string public constant NAME = "HintHelpers";

	ISortedTroves public sortedTroves;
	IVault public vault;

	// --- Events ---

	event SortedTrovesAddressChanged(address _sortedTrovesAddress);
	event VaultAddressChanged(address _vaultAddress);

	// --- Dependency setters ---

	function setAddresses(address _sortedTrovesAddress, address _vaultAddress) external onlyOwner {
		checkContract(_sortedTrovesAddress);
		checkContract(_vaultAddress);

		sortedTroves = ISortedTroves(_sortedTrovesAddress);
		vault = IVault(_vaultAddress);

		emit SortedTrovesAddressChanged(_sortedTrovesAddress);
		emit VaultAddressChanged(_vaultAddress);

		renounceOwnership();
	}

	// --- Functions ---

	/* getRedemptionHints() - Helper function for finding the right hints to pass to redeemCollateral().
	 *
	 * It simulates a redemption of `_PAIamount` to figure out where the redemption sequence will start and what state the final Trove
	 * of the sequence will end up in.
	 *
	 * Returns three hints:
	 *  - `firstRedemptionHint` is the address of the first Trove with ICR >= MCR (i.e. the first Trove that will be redeemed).
	 *  - `partialRedemptionHintNICR` is the final nominal ICR of the last Trove of the sequence after being hit by partial redemption,
	 *     or zero in case of no partial redemption.
	 *  - `truncatedPAIamount` is the maximum amount that can be redeemed out of the the provided `_PAIamount`. This can be lower than
	 *    `_PAIamount` when redeeming the full amount would leave the last Trove of the redemption sequence with less net debt than the
	 *    minimum allowed value (i.e. MIN_NET_DEBT).
	 *
	 * The number of Troves to consider for redemption can be capped by passing a non-zero value as `_maxIterations`, while passing zero
	 * will leave it uncapped.
	 */

	function getRedemptionHints(
		uint256 _PAIamount,
		uint256 _price,
		uint256 _maxIterations
	)
		external
		view
		returns (
			address firstRedemptionHint,
			uint256 partialRedemptionHintNICR,
			uint256 truncatedPAIamount
		)
	{
		ISortedTroves sortedTrovesCached = sortedTroves;

		uint256 remainingPAI = _PAIamount;
		address currentTroveUser = sortedTrovesCached.getLast();

		while (
			currentTroveUser != address(0) && vault.getCurrentICR(currentTroveUser, _price) < MCR
		) {
			currentTroveUser = sortedTrovesCached.getPrev(currentTroveUser);
		}

		firstRedemptionHint = currentTroveUser;

		if (_maxIterations == 0) {
			_maxIterations = MAX_UINT;
		}

		while (currentTroveUser != address(0) && remainingPAI > 0 && _maxIterations > 0) {
			_maxIterations--;

			uint256 netPAIDebt = vault.getTroveDebt(currentTroveUser).add(
				vault.getPendingDebtReward(currentTroveUser)
			);

			if (netPAIDebt > remainingPAI) {
				uint256 maxRedeemablePAI = BaseMath._min(remainingPAI, netPAIDebt);

				uint256 coll = vault.getTroveColl(currentTroveUser).add(
					vault.getPendingCollReward(currentTroveUser)
				);

				uint256 newColl = coll.sub(maxRedeemablePAI.mul(DECIMAL_PRECISION).div(_price));
				uint256 newDebt = netPAIDebt.sub(maxRedeemablePAI);

				partialRedemptionHintNICR = BaseMath._computeNominalCR(newColl, newDebt);

				remainingPAI = remainingPAI.sub(maxRedeemablePAI);
			} else {
				remainingPAI = remainingPAI.sub(netPAIDebt);
			}

			currentTroveUser = sortedTrovesCached.getPrev(currentTroveUser);
		}

		truncatedPAIamount = _PAIamount.sub(remainingPAI);
	}

	/* getApproxHint() - return address of a Trove that is, on average, (length / numTrials) positions away in the 
    sortedTroves list from the correct insert position of the Trove to be inserted. 

    Note: The output address is worst-case O(n) positions away from the correct insert position, however, the function 
    is probabilistic. Input can be tuned to guarantee results to a high degree of confidence, e.g:

    Submitting numTrials = k * sqrt(length), with k = 15 makes it very, very likely that the ouput address will 
    be <= sqrt(length) positions away from the correct insert position.
    */
	function getApproxHint(
		uint256 _CR,
		uint256 _numTrials,
		uint256 _inputRandomSeed
	)
		external
		view
		returns (
			address hintAddress,
			uint256 diff,
			uint256 latestRandomSeed
		)
	{
		uint256 arrayLength = vault.getTroveOwnersCount();

		if (arrayLength == 0) {
			return (address(0), 0, _inputRandomSeed);
		}

		hintAddress = sortedTroves.getLast();
		diff = BaseMath._getAbsoluteDifference(_CR, vault.getNominalICR(hintAddress));
		latestRandomSeed = _inputRandomSeed;

		uint256 i = 1;

		while (i < _numTrials) {
			latestRandomSeed = uint256(keccak256(abi.encodePacked(latestRandomSeed)));

			uint256 arrayIndex = latestRandomSeed % arrayLength;
			address currentAddress = vault.getTroveFromTroveOwnersArray(arrayIndex);
			uint256 currentNICR = vault.getNominalICR(currentAddress);

			// check if abs(current - CR) > abs(closest - CR), and update closest if current is closer
			uint256 currentDiff = BaseMath._getAbsoluteDifference(currentNICR, _CR);

			if (currentDiff < diff) {
				diff = currentDiff;
				hintAddress = currentAddress;
			}
			i++;
		}
	}

	function computeNominalCR(uint256 _coll, uint256 _debt) external pure returns (uint256) {
		return BaseMath._computeNominalCR(_coll, _debt);
	}

	function computeCR(
		uint256 _coll,
		uint256 _debt,
		uint256 _price
	) external pure returns (uint256) {
		return BaseMath._computeCR(_coll, _debt, _price);
	}
}
