pragma solidity 0.8.3;

// CAUTION
// This version of SafeMath should only be used with Solidity 0.8 or later,
// because it relies on the compiler's built in overflow checks.
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

import "../core/CoreRef.sol";

import "../dependencies/BaseMath.sol";
import "../dependencies/CheckContract.sol";
import "../interfaces/IMYOStaking.sol";

contract MYOStaking is CoreRef, IMYOStaking {
	using SafeMath for uint256;

	// --- Data ---
	string public constant NAME = "MYOStaking";

	mapping(address => uint256) public stakes;
	uint256 public totalMYOStaked;

	uint256 public F_GEM; // Running sum of GEM fees per-MYO-staked
	uint256 public F_PAI; // Running sum of PAI fees per-MYO-staked

	// User snapshots of F_GEM and F_PAI, taken at the point at which their latest deposit was made
	mapping(address => Snapshot) public snapshots;

	struct Snapshot {
		uint256 F_GEM_Snapshot;
		uint256 F_PAI_Snapshot;
	}

	// ILQTYToken public lqtyToken;
	// ILUSDToken public lusdToken;

	// --- Events ---
	event StakeChanged(address indexed staker, uint256 newStake);
	event StakingGainsWithdrawn(address indexed staker, uint256 PAIGain, uint256 GEMGain);
	event F_GEMUpdated(uint256 _F_GEM);
	event F_PAIUpdated(uint256 _F_PAI);
	event TotalMYOStakedUpdated(uint256 _totalMYOStaked);
	event PAISent(address _account, uint256 _amount);
	event GEMSent(address _account, uint256 _amount);
	event StakerSnapshotsUpdated(address _staker, uint256 _F_GEM, uint256 _F_PAI);

	constructor(address _core) CoreRef(_core) {}

	// --- Functions ---

	// If caller has a pre-existing stake, send any accumulated GEM and PAI gains to them.
	function stake(uint256 _MYOamount) external override {
		_requireNonZeroAmount(_MYOamount);

		uint256 currentStake = stakes[msg.sender];

		uint256 GEMGain;
		uint256 PAIGain;
		// Grab any accumulated ETH and LUSD gains from the current stake
		if (currentStake != 0) {
			GEMGain = _getPendingGEMGain(msg.sender);
			PAIGain = _getPendingPAIGain(msg.sender);
		}

		_updateUserSnapshots(msg.sender);

		uint256 newStake = currentStake.add(_MYOamount);

		// Increase user’s stake and total MYO staked
		stakes[msg.sender] = newStake;
		totalMYOStaked = totalMYOStaked.add(_MYOamount);
		emit TotalMYOStakedUpdated(totalMYOStaked);

		// Transfer MYO from caller to this contract
		myoToken().sendToMYOStaking(msg.sender, _MYOamount);

		emit StakeChanged(msg.sender, newStake);
		emit StakingGainsWithdrawn(msg.sender, PAIGain, GEMGain);

		// Send accumulated LUSD and ETH gains to the caller
		if (currentStake != 0) {
			_sendPAIGainToUser(PAIGain);
			_sendGEMGainToUser(GEMGain);
		}
	}

	// Unstake the MYO and send the it back to the caller, along with their accumulated PAI & GEM gains.
	// If requested amount > stake, send their entire stake.
	function unstake(uint256 _MYOamount) external override {
		uint256 currentStake = stakes[msg.sender];
		_requireUserHasStake(currentStake);

		// Grab any accumulated ETH and LUSD gains from the current stake
		uint256 GEMGain = _getPendingGEMGain(msg.sender);
		uint256 PAIGain = _getPendingPAIGain(msg.sender);

		_updateUserSnapshots(msg.sender);

		if (_MYOamount > 0) {
			uint256 MYOToWithdraw = BaseMath._min(_MYOamount, currentStake);
			uint256 newStake = currentStake.sub(MYOToWithdraw);

			// Decrease user's stake and total LQTY staked
			stakes[msg.sender] = newStake;
			totalMYOStaked = totalMYOStaked.sub(MYOToWithdraw);
			emit TotalMYOStakedUpdated(totalMYOStaked);

			// Transfer unstaked LQTY to user
			myoToken().transfer(msg.sender, MYOToWithdraw);

			emit StakeChanged(msg.sender, newStake);
		}

		emit StakingGainsWithdrawn(msg.sender, PAIGain, GEMGain);

		// Send accumulated LUSD and ETH gains to the caller
		_sendPAIGainToUser(PAIGain);
		_sendGEMGainToUser(GEMGain);
	}

	// --- Reward-per-unit-staked increase functions. Called by core contracts ---

	function increaseF_GEM(uint256 _GEMFee) external override {
		_requireCallerIsActivePool();
		uint256 GEMFeePerMYOStaked;

		if (totalMYOStaked > 0) {
			GEMFeePerMYOStaked = _GEMFee.mul(DECIMAL_PRECISION).div(totalMYOStaked);
		}

		F_GEM = F_GEM.add(GEMFeePerMYOStaked);
		emit F_GEMUpdated(F_GEM);
	}

	function increaseF_PAI(uint256 _PAIFee) external override {
		_requireCallerIsBorrowerOperations();
		uint256 PAIFeePerMYOStaked;

		if (totalMYOStaked > 0) {
			PAIFeePerMYOStaked = _PAIFee.mul(DECIMAL_PRECISION).div(totalMYOStaked);
		}

		F_PAI = F_PAI.add(PAIFeePerMYOStaked);
		emit F_PAIUpdated(F_PAI);
	}

	function getAccruedGEMFees() external view override returns (uint256) {
		return F_GEM;
	}

	function getAccruedPAIFees() external view override returns (uint256) {
		return F_PAI;
	}

	// --- Pending reward functions ---

	function getPendingGEMGain(address _user) external view override returns (uint256) {
		return _getPendingGEMGain(_user);
	}

	function _getPendingGEMGain(address _user) internal view returns (uint256) {
		uint256 F_GEM_Snapshot = snapshots[_user].F_GEM_Snapshot;
		uint256 GEMGain = stakes[_user].mul(F_GEM.sub(F_GEM_Snapshot)).div(DECIMAL_PRECISION);
		return GEMGain;
	}

	function getPendingPAIGain(address _user) external view override returns (uint256) {
		return _getPendingPAIGain(_user);
	}

	function _getPendingPAIGain(address _user) internal view returns (uint256) {
		uint256 F_PAI_Snapshot = snapshots[_user].F_PAI_Snapshot;
		uint256 PAIGain = stakes[_user].mul(F_PAI.sub(F_PAI_Snapshot)).div(DECIMAL_PRECISION);
		return PAIGain;
	}

	// --- Internal helper functions ---

	function _updateUserSnapshots(address _user) internal {
		snapshots[_user].F_GEM_Snapshot = F_GEM;
		snapshots[_user].F_PAI_Snapshot = F_PAI;
		emit StakerSnapshotsUpdated(_user, F_GEM, F_PAI);
	}

	function _sendGEMGainToUser(uint256 _GEMGain) internal {
		emit GEMSent(msg.sender, _GEMGain);
		bool success = gemToken().transfer(msg.sender, _GEMGain);
		require(success, "MYOStaking: Failed to send accumulated GEMGain");
	}

	function _sendPAIGainToUser(uint256 PAIGain) internal {
		emit PAISent(msg.sender, PAIGain);
		bool success = paiToken().transfer(msg.sender, PAIGain);
		require(success, "MYOStaking: Failed to send accumulated PAIGain");
	}

	// --- 'require' functions ---

	function _requireCallerIsActivePool() internal view {
		require(msg.sender == address(activePool()), "MYOStaking: caller is not ActivePool");
	}

	function _requireCallerIsVault() internal view {
		require(msg.sender == address(vault()), "MYOStaking: caller is not Vault");
	}

	function _requireCallerIsBorrowerOperations() internal view {
		require(msg.sender == address(borrowerOperations()), "MYOStaking: caller is not BorrowerOps");
	}

	// function _requireCallerIsActivePool() internal view {
	// 	require(msg.sender == activePoolAddress, "LQTYStaking: caller is not ActivePool");
	// }

	function _requireUserHasStake(uint256 currentStake) internal pure {
		require(currentStake > 0, "MYOStaking: User must have a non-zero stake");
	}

	function _requireNonZeroAmount(uint256 _amount) internal pure {
		require(_amount > 0, "MYOStaking: Amount must be non-zero");
	}
}
