	function getEntireSystemColl() public view returns (uint256 entireSystemColl) {
		uint256 activeColl = activePool().getColl();
		uint256 liquidatedColl = defaultPool().getColl();

		return activeColl.add(liquidatedColl);
	}

	function getEntireSystemDebt() public view returns (uint256 entireSystemDebt) {
		uint256 activeDebt = activePool().getDebt();
		uint256 closedDebt = defaultPool().getDebt();

		return activeDebt.add(closedDebt);
	}

	function _getTCR(uint256 _price) internal view returns (uint256 TCR) {
		uint256 entireSystemColl = getEntireSystemColl();
		uint256 entireSystemDebt = getEntireSystemDebt();

		TCR = BaseMath._computeCR(entireSystemColl, entireSystemDebt, _price);

		return TCR;
	}

	function _checkRecoveryMode(uint256 _price) internal view returns (bool) {
		uint256 TCR = _getTCR(_price);

		return TCR < CCR;
	}

	function _getNetCollateral(uint256 _collateral) internal pure returns (uint256) {
		return
			_collateral.sub(
				DAI_GAS_COMPENSATION,
				"Core: Insuficient Net Collateral to cover GAS Comp"
			);
	}

	function _getGrossColl(uint256 _debt) internal pure returns (uint256) {
		return _debt.add(DAI_GAS_COMPENSATION);
	}

	function _requireUserAcceptsFee(
		uint256 _gemFee,
		uint256 _gemAmount,
		uint256 _maxFeePercentage
	) internal pure {
		uint256 feePercentage = _gemFee.mul(DECIMAL_PRECISION).div(_gemAmount);
		require(feePercentage <= _maxFeePercentage, "Fee exceeded provided maximum");
	}
}
