pragma solidity 0.8.3;

// CAUTION
// This version of SafeMath should only be used with Solidity 0.8 or later,
// because it relies on the compiler's built in overflow checks.
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

import "../dependencies/CheckContract.sol";
import "../interfaces/ILockupContractFactory.sol";
import "./LockupContract.sol";

/*
 * The LockupContractFactory deploys LockupContracts - its main purpose is to keep a registry of valid deployed
 * LockupContracts.
 *
 * This registry is checked by MYOToken when the Liquity deployer attempts to transfer MYO tokens. During the first year
 * since system deployment, the Liquity deployer is only allowed to transfer MYO to valid LockupContracts that have been
 * deployed by and recorded in the LockupContractFactory. This ensures the deployer's MYO can't be traded or staked in the
 * first year, and can only be sent to a verified LockupContract which unlocks at least one year after system deployment.
 *
 * LockupContracts can of course be deployed directly, but only those deployed through and recorded in the LockupContractFactory
 * will be considered "valid" by MYOToken. This is a convenient way to verify that the target address is a genuine
 * LockupContract.
 */

contract LockupContractFactory is ILockupContractFactory, Ownable, CheckContract {
	using SafeMath for uint256;

	// --- Data ---
	string public constant NAME = "LockupContractFactory";

	uint256 public constant SECONDS_IN_ONE_YEAR = 31536000;

	address public myoTokenAddress;

	mapping(address => address) public lockupContractToDeployer;

	// --- Functions ---

	function setMYOTokenAddress(address _myoTokenAddress) external override onlyOwner {
		checkContract(_myoTokenAddress);

		myoTokenAddress = _myoTokenAddress;
		emit MYOTokenAddressSet(_myoTokenAddress);

		renounceOwnership();
	}

	function deployLockupContract(address _beneficiary, uint256 _unlockTime) external override {
		address myoTokenAddressCached = myoTokenAddress;
		_requireMYOAddressIsSet(myoTokenAddressCached);
		LockupContract lockupContract = new LockupContract(myoTokenAddressCached, _beneficiary, _unlockTime);

		lockupContractToDeployer[address(lockupContract)] = msg.sender;
		emit LockupContractDeployedThroughFactory(address(lockupContract), _beneficiary, _unlockTime, msg.sender);
	}

	function isRegisteredLockup(address _contractAddress) public view override returns (bool) {
		return lockupContractToDeployer[_contractAddress] != address(0);
	}

	// --- 'require'  functions ---
	function _requireMYOAddressIsSet(address _myoTokenAddress) internal pure {
		require(_myoTokenAddress != address(0), "LCF: MYO Address is not set");
	}
}
